#include <algorithm>#include <iostream>#include <vector>#define INF 1000000000000000007int main(){    int  n;    std::cin >> n;    std::vector<long long> r(n);    for(int i = 0; i < n; ++i){        std::cin >> r.at(i);    }    long long min = INF, diffMax = -INF;    for(int i = 0; i < n; ++i){        diffMax = std::max(diffMax, r.at(i) - min);        min = std::min(min, r.at(i));    }    std::cout << diffMax << std::endl;    return 0;}