#include <iostream>#include <utility>#include <vector>void print(std::vector<int>& readVec){    int n = readVec.size();    for(int i = 0; i < n; ++i){        std::cout << readVec.at(i);        if(n - 1 != i){            std::cout << " ";        }else{            std::cout << std::endl;        }    }}int bubbleSort(std::vector<int>& readVec){    int n = readVec.size(), count = 0;    bool flg = true; // existence of the left-adjacent element less than an interest one.    while(flg){        flg = false;        for(int j = n - 1; 0 < j; --j){            if(readVec.at(j) < readVec.at(j - 1)){                std::swap(readVec.at(j), readVec.at(j - 1));                flg = true;                ++count;            }        }    }    return count;}int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    int count = bubbleSort(a);    print(a);    std::cout << count << std::endl;    return 0;}