#include <iostream>#include <utility>#include <vector>void print(std::vector<int>& readVec){    int n = readVec.size();    for(int i = 0; i < n; ++i){        std::cout << readVec.at(i);        if(n - 1 != i){            std::cout << " ";        }else{            std::cout << std::endl;        }    }}int selectionSort(std::vector<int>& readVec){    int n = readVec.size(), count = 0;    for(int i = 0; i < n; ++i){        int minJ = i;        for(int j = i; j < n; ++j){            if(readVec.at(j) < readVec.at(minJ)){                minJ = j;            }        }                if(i != minJ){            std::swap(readVec.at(i), readVec.at(minJ));            ++count;        }    }    return count;}int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    int count = selectionSort(a);    print(a);    std::cout << count << std::endl;    return 0;}