#include <iostream>#include <vector>void showVector(std::vector<int> readVec){    int length = readVec.size();    for(int i = 0; i < length; ++i){        if(length - 1 != i){            std::cout << readVec.at(i) << " ";        }else{            std::cout << readVec.at(i);        }    }        std::cout << std::endl;}int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    showVector(a);    int i, j, tmp;    for(i = 1; i < n; ++i){        tmp = a.at(i);        j = i - 1;        while(0 <= j && tmp < a.at(j)){            a.at(j + 1) = a.at(j);            --j;        }        a.at(j + 1) = tmp;            showVector(a);        }        return 0;}