#include <iostream>#include <vector>#include <algorithm>#include <cmath>int main(){    int n;    std::cin >> n;    int maxVal = -2 * std::pow(10, 9);    int minVal = 2 * std::pow(10, 9);        int val;    for(int i = 0; i < n; ++i){        std::cin >> val;        maxVal = std::max(maxVal, val - minVal);        minVal = std::min(minVal, val);    }    std::cout << maxVal << std::endl;    return 0;}