#include <iostream>#include <iomanip>#include <cmath>#include <numeric>#include <vector>int main(){    int n;    while(std::cin >> n){        if(0 == n){            break;        }else{            std::vector<double> s(n);            double sum;            for(int i = 0; i < n; ++i){                std::cin >> s.at(i);            }            double avg = std::accumulate(s.begin(), s.end(), 0.0)/s.size();            double sigma_sqrd = 0;            for(int i = 0; i < n; ++i){                sigma_sqrd += std::pow(s.at(i) - avg, 2);            }            std::cout << std::fixed << std::setprecision(4) << std::sqrt(sigma_sqrd/s.size()) << std::endl;        }    }    return 0;}